import React, { useRef } from 'react';
import CardSection from './CardSection';
import Elements from './Elements';
import GIF from './GIF';
import Roadmap from './Roadmap';
import Feedback from './Feedback';
import './Homepage.css';

const Homepage = () => {
  const gifRef = useRef(null);
  const elementsRef = useRef(null);
  const roadmapRef = useRef(null);
  const feedbackRef = useRef(null);

  return (
    <div className="homepage">
      <CardSection id="GIF" title="Introduction" content="Welcome to the 'Road to Independence'. Explore the journey and resources available for visually impaired individuals.">
        <GIF scrollToSection={(id) => gifRef.current.scrollToSection(id)} ref={gifRef} />
      </CardSection>
      <CardSection id="Elements" title="Core Features" ref={elementsRef}>
        <Elements />
      </CardSection>
      <CardSection id="Roadmap" title="Roadmap" ref={roadmapRef}>
        <Roadmap />
      </CardSection>
      <CardSection id="Feedback" title="Feedback" ref={feedbackRef}>
        <Feedback />
      </CardSection>
    </div>
  );
};

export default Homepage;
