import React, { useState } from 'react';
import { useHighContrast } from '../../../components/HighContrastContext';
import './Encourage.css';

const Encourage = () => {
  const { isHighContrast } = useHighContrast();

  const generateRandomPosition = () => {
    return {
      top: `${Math.random() * 70}%`,
      left: `${Math.random() * 70}%`,
      transform: 'translate(-50%, -50%)',
    };
  };

  const [bubbles, setBubbles] = useState([
    // ...（保留原有气泡数组）...
    {
      id: 1,
      content: "If people say how can you live with blindness.",
      poppedContent: "I lived, bitch. Blindness is just a part of your life. Keep moving forward. Make people's mouths shout and watch you become the GOAT.",
      popped: false,
      style: generateRandomPosition(),
    },
    {
      id: 2,
      content: "You have a name other than BLIND",
      poppedContent: "Don’t let your disability be your personality. Don’t be afraid of trying more times than others. You know you can earn it. Let others remember your name, not a stupid label.",
      popped: false,
      style: generateRandomPosition(),
    },
    {
      id: 3,
      content: "Don't let sight loss destroy you",
      poppedContent: "If you see sight loss as devastating, it will be devastating. We only live once. Sometimes, the burden of reality is suffocating. But you still can choose not to be devastated and find something valuable in your life.",
      popped: false,
      style: generateRandomPosition(),
    },
    {
      id: 4,
      content: "When you feel lonely",
      poppedContent: "I’m not the first and I won’t be the last. Some hardships are inevitable, but we don't have to face them alone. With connections and community, we can always find the bright part in the darkest days.",
      popped: false,
      style: generateRandomPosition(),
    },
    {
      id: 5,
      content: "Whether you are sighted or visually impaired",
      poppedContent: "You might be the envy of others. What you want to let go of could be something others desperately wish for. Don’t choose to be a coward. The cards are in your hands. Hold on to them tightly and play them wisely.",
      popped: false,
      style: generateRandomPosition(),
    },
    {
      id: 6,
      content: "If you need the courage to begin your journey",
      poppedContent: "Little by little, the bird builds his nest. Rome wasn’t built in a day. Under the new rules, you are building your own castle. Be patient and confident. Like a bird, let's build it little by little.",
      popped: false,
      style: generateRandomPosition(),
    },
    {
      id: 7,
      content: "If people underestimate your ability",
      poppedContent: "Lack of sight doesn’t equate to lack of intelligence. Don’t let others tell you what you can do. If you want to go to Princeton for physics and math, your path is clearer than most.",
      popped: false,
      style: generateRandomPosition(),
    },
    {
      id: 8,
      content: "When you're stuck in your room",
      poppedContent: "Go to school or stay and be a couch potato. Imagine sinking into your couch and letting your family handle all your daily tasks for forty years. If that's not the future you want, then get up and take action.",
      popped: false,
      style: generateRandomPosition(),
    },
    {
      id: 9,
      content: "You know what blind people lose but never miss?",
      poppedContent: "The ability to be shallow. Blind people don’t judge others by their appearance. Visually impaired individuals can both give and receive love. Personality is their primary criterion, not appearance.",
      popped: false,
      style: generateRandomPosition(),
    },
    {
      id: 10,
      content: "Don’t think too much about how you were born",
      poppedContent: "We’re all dead in the end. However we begin our life, it becomes truly fulfilling if we can proudly say 'I have lived'",
      popped: false,
      style: generateRandomPosition(),
    },
  ]);

  const handleBubbleClick = (id) => {
    const updatedBubbles = bubbles.map((bubble) =>
      bubble.id === id
        ? {
            ...bubble,
            popped: !bubble.popped,
            style: bubble.popped ? generateRandomPosition() : adjustPosition(bubble.style),
          }
        : bubble
    );
    setBubbles(updatedBubbles);
    playBurstSound(); // 播放爆破音效
  };

  const adjustPosition = (style) => {
    let top = parseFloat(style.top);
    let left = parseFloat(style.left);
    const offset = 225; // Adjusted to ensure bubble stays within bounds

    if (top < offset) top = offset;
    if (top > 100 - offset) top = 100 - offset;
    if (left < offset) left = offset;
    if (left > 100 - offset) left = 100 - offset;

    return {
      top: `${top}%`,
      left: `${left}%`,
      transform: 'translate(-50%, -50%)',
    };
  };

  const playBurstSound = () => {
    const audio = new Audio(`${process.env.PUBLIC_URL}/burst.mp3`);
    audio.volume = 1; 
    audio.play();
  };

  return (
    <div className={`encourage-page ${isHighContrast ? 'high-contrast' : ''}`}>
      <div className="page-header">
        <h1>Encouragement</h1>
        <p>Click on the bubble to break the nonsense.</p>
      </div>
      <div className="bubble-container">
        <div className="background-overlay"></div>
        {bubbles.map((bubble) => (
          <div
            key={bubble.id}
            className={`bubble ${bubble.popped ? 'popped' : ''}`}
            style={bubble.style}
            onClick={() => handleBubbleClick(bubble.id)}
          >
            <div className="bubble-overlay">
              <div className="bubble-content">
                {bubble.popped ? (
                  <div dangerouslySetInnerHTML={{ __html: bubble.poppedContent }} />
                ) : (
                  <p>{bubble.content}</p>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Encourage;
