import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faLink } from '@fortawesome/free-solid-svg-icons';
import './Elements.css';

const elements = [
  // Updated elements array with credit fields
  { id: 1, title: 'Roadmap', thumbnail: 'independence.jpg', details: 'The roadmap outlines a six-step journey, starting from obtaining vision assessment results to empowering individuals with similar experiences. Each step is designed to progressively build both mental and physical skills, leading to greater independence for visually impaired individuals.', link: '', credit: 'Image credit: Eric W. Pohl' },
  { id: 2, title: 'History', thumbnail: 'history.jpg', details: 'From early marginalization to today’s comprehensive support and technological advancements, explore how society has progressively recognized and integrated the visually impaired community.', link: '/insights/history', credit: 'Image credit: Wayfinder Family Services' },
  { id: 3, title: 'Technology', thumbnail: 'technology1.jpg', details: 'Explore the various technologies designed to assist visually impaired individuals. From public facilities to essential tools, and advanced electronic software to innovative mobile applications.', link: '/support/technology', credit: 'Image credit: Grant Marketing' },
  { id: 4, title: 'Encourage', thumbnail: 'forum.jpg', details: 'The Encourage Page showcases a collection of uplifting and motivational messages. This page aims to inspire everyone, fostering a sense of independence and encouraging the pursuit of greater goals.', link: '/support/encourage', credit: 'Image credit: Chris Montgomery, unsplash.com' },
  { id: 5, title: 'Service', thumbnail: 'service.jpg', details: 'This program search tool is designed to help users find services that meet their specific needs. Users can filter results to discover relevant services and resources tailored to their preferences.', link: '/support/service', credit: 'Image credit: Florida Outreach Center for the Blind' },
  { id: 6, title: 'Learn', thumbnail: 'https://www.nfb.org/sites/default/files/images/nfb/publications/bm/bm12/bm1203/chris-nusbaum.gif', details: 'In Learn page, you will explore a wealth of knowledge about visually impaired individuals. Delve into various aspects that shed light on their experiences, challenges, and achievements.', link: '/insights/learn', credit: 'Image credit: [National Federation of the Blind](https://www.nfb.org)' },
];

const Elements = () => {
  const [activeElements, setActiveElements] = useState({});

  const handleElementClick = (elementId) => {
    setActiveElements((prevActiveElements) => ({
      ...prevActiveElements,
      [elementId]: !prevActiveElements[elementId]
    }));
  };

  return (
    <div className="elements">
      {elements.map((element) => (
        <div key={element.id} className="element-card">
          {activeElements[element.id] ? (
            <div className="element-details">
              <div
                className="background-blur"
                style={{ backgroundImage: `url(${element.thumbnail})` }}
              ></div>
              <div className="details-content">
                <p>{element.details}</p>
                {element.link && (
                  <Link to={element.link} className="link-button">
                    <FontAwesomeIcon icon={faLink} size="lg" />
                    <span>Visit Page</span>
                  </Link>
                )}
                <button onClick={() => handleElementClick(element.id)}>Back</button>
              </div>
            </div>
          ) : (
            <>
              <img src={element.thumbnail} alt={element.title} className="element-thumbnail" />
              {element.credit && <p className="image-credit">{element.credit}</p>}
              <h3>{element.title}</h3>
              <button onClick={() => handleElementClick(element.id)}>Click for details</button>
            </>
          )}
        </div>
      ))}
    </div>
  );
};

export default Elements;
