import React, { useState } from 'react';
import './GIF.css';

const GIF = () => {
  const [showNavigation, setShowNavigation] = useState(false);

  const scrollToSection = (id) => {
    const element = document.getElementById(id);
    console.log('Scrolling to:', element);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <div className="content-area">
      <div className="container">
        <img className="gif" src="/Cane walking.gif" alt="Blind person walking with a cane" />
        <div className="top-right-container">
          <div className="top-right-text">
            <p>Journey of visually impaired persons</p>
            <hr className="separator" />
            <p className="smaller-text">A journey that you might never SEE</p>
          </div>
        </div>
      </div>
      <div id="navigation" className={showNavigation ? 'show' : 'hide'}>
        <p className="navigation-info">Click on these elements to explore the page:</p>
        <ul className="navigation-list">
          <li onClick={() => scrollToSection('Elements')}>Core Features</li>
          <li onClick={() => scrollToSection('Roadmap')}>Roadmap</li>
          <li onClick={() => scrollToSection('Feedback')}>Feedback</li>
        </ul>
      </div>
      <div id="Elements" className="section">
        <h2>Core Features</h2>
        <p>Content for Core Features...</p>
      </div>
      <div id="Roadmap" className="section">
        <h2>Roadmap</h2>
        <p>Content for Roadmap...</p>
      </div>
      <div id="Feedback" className="section">
        <h2>Feedback</h2>
        <p>Content for Feedback...</p>
      </div>
    </div>
  );
};

export default GIF;
