import React, { useState } from 'react';
import './Forum.css';

function forum() {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedTopic, setSelectedTopic] = useState(null);
  const [posts, setPosts] = useState([
    { id: 1, topic: 'General', title: 'Welcome to the forum', content: 'This is the first post.', author: 'Admin' },
    { id: 2, topic: 'General', title: 'Second post', content: 'This is another post.', author: 'User1' },
    { id: 3, topic: 'Technology', title: 'Tech post', content: 'Discussing technology.', author: 'TechGuru' },
  ]);

  const [newPost, setNewPost] = useState({ name: '', title: '', content: '', topic: '' });

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleTopicSelect = (topic) => {
    setSelectedTopic(topic);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewPost(prevPost => ({
      ...prevPost,
      [name]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setPosts([...posts, { id: posts.length + 1, ...newPost }]);
    setNewPost({ name: '', title: '', content: '', topic: '' });
  };

  const filteredPosts = posts.filter((post) =>
    post.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const topics = [...new Set(posts.map(post => post.topic))];

  return (
    <div className="forum-page">
      <header>
        <input
          type="text"
          placeholder="Search for posts..."
          value={searchTerm}
          onChange={handleSearch}
        />
      </header>
      <div className="main-content">
        <aside className="topics-list">
          <ul>
            {topics.map((topic, index) => (
              <li
                key={index}
                className={selectedTopic === topic ? 'selected' : ''}
                onClick={() => handleTopicSelect(topic)}
              >
                {topic}
              </li>
            ))}
          </ul>
        </aside>
        <section className="posts-list">
          {filteredPosts
            .filter((post) => !selectedTopic || post.topic === selectedTopic)
            .map((post) => (
              <div key={post.id} className="post">
                <h3>{post.title}</h3>
                <p>{post.content}</p>
                <p><strong>{post.author}</strong></p>
              </div>
            ))}
        </section>
      </div>
      <footer>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="name"
            placeholder="Your Name"
            value={newPost.name}
            onChange={handleInputChange}
            required
          />
          <input
            type="text"
            name="title"
            placeholder="Post Title"
            value={newPost.title}
            onChange={handleInputChange}
            required
          />
          <textarea
            name="content"
            placeholder="Write your post here..."
            value={newPost.content}
            onChange={handleInputChange}
            required
          />
          <select name="topic" value={newPost.topic} onChange={handleInputChange} required>
            <option value="">Select Topic</option>
            {topics.map((topic, index) => (
              <option key={index} value={topic}>
                {topic}
              </option>
            ))}
          </select>
          <button type="submit">Submit</button>
        </form>
      </footer>
    </div>
  );
}

export default forum;
