import React, { useState } from 'react';
import CardSection from './CardSection';
import Elements from './Elements';
import GIF from './GIF';
import Roadmap from './Roadmap';
import Feedback from './Feedback';
import './Homepage.css';

const Homepage = () => {
  const [showElements, setShowElements] = useState(false);

  const handleContinue = () => {
    setShowElements(true);
  };

  return (
    <div className="homepage">
      <CardSection title="Introduction" content="Welcome to the 'Road to Independence'. Explore the journey and resources available for visually impaired individuals.">
        <GIF onContinue={handleContinue} />
      </CardSection>
      {showElements && (
        <CardSection title="Core Features">
          <Elements />
        </CardSection>
      )}
      <CardSection title="Roadmap">
        <Roadmap />
      </CardSection>
      <CardSection>
        <Feedback />
      </CardSection>
    </div>
  );
};

export default Homepage;
