import React from 'react';
import './Roadmap.css';

const Roadmap = () => {
  const handleMouseMove = (e) => {
    const mouseX = e.nativeEvent.offsetX;
    const mouseY = e.nativeEvent.offsetY;

    const zoomIcon = document.getElementById('zoom-icon');
    if (zoomIcon) {
      zoomIcon.style.left = `${mouseX}px`;
      zoomIcon.style.top = `${mouseY}px`;
    }
  };

  return (
    <div className="roadmap-container">
      <div className="image-container" onMouseMove={handleMouseMove}>
        <img
          src={`${process.env.PUBLIC_URL}/Road to the independence.png`}
          alt="Road to Independence"
          className="small-image"
        />
        <div id="zoom-icon" className="zoom-icon">
          <i className="fa fa-search-plus"></i>
        </div>
      </div>
    </div>
  );
};

export default Roadmap;
