import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';
import './MiniSearch.css';
import MiniSearch from 'minisearch';
import keywordLists from '../config/keywordLists';

const MiniSearchComponent = ({ data }) => {
  const [query, setQuery] = useState('');
  const miniSearch = new MiniSearch({ fields: ['title', 'link'] });
  const navigate = useNavigate();

  useEffect(() => {
    miniSearch.addAll(data);
  }, [data]);

  const handleSearch = () => {
    const trimmedQuery = query.trim(); // Trim whitespace from query
    if (trimmedQuery.length > 0) {
      const results = miniSearch.search(trimmedQuery);
      if (results.length > 0 && results[0].title) {
        const firstResult = results[0];
        const page = getPageFromKeyword(firstResult.title.toLowerCase());
        navigate(page);
      }
    }
  };

  const getPageFromKeyword = (keyword) => {
    for (let page in keywordLists) {
      if (keywordLists[page].includes(keyword)) {
        return `/${page}`;
      }
    }
    return '/';
  };

  return (
    <div className="search-container">
      <input
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Search..."
        className="search-input"
        onKeyPress={(e) => {
          if (e.key === 'Enter') {
            handleSearch();
          }
        }}
      />
      <FontAwesomeIcon
        icon={faSearch}
        className="search-icon"
        onClick={handleSearch} // 确保点击图标时调用 handleSearch 函数
      />
    </div>
  );
};

export default MiniSearchComponent;
