import React, { useState } from 'react';
import Header from './components/Header';
import Footer from './components/Footer';
import Navbar from './components/Navbar';
import BackToTopButton from './components/BackToTopButton';
import Homepage from './pages/Homepage/Homepage';
import Forum from './pages/Forum/Forum';
import VisionFAQs from './pages/VisionFAQs/VisionFAQs';
import SearchResults from './components/SearchResults';

const App = () => {
  const [searchResults, setSearchResults] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const navigate = useNavigate();

  const handleSearch = (query) => {
    setSearchQuery(query);
    if (query.toLowerCase() === 'forum' || query.toLowerCase() === 'post') {
      navigate('/forum');
    } else {
      const results = performSearch(query);
      setSearchResults(results);
      navigate('/search');
    }
  };

  const performSearch = (query) => {
    // Example search logic - replace with your own logic.
    const items = [
      'Independence Techniques',
      'Accessible Tools',
      'Stories of Independence',
      // Add more items or fetch from an API.
    ];

    return items.filter(item =>
      item.toLowerCase().includes(query.toLowerCase())
    );
  };

  return (
    <BrowserRouter>
      <div className="App">
        <Header onSearch={handleSearch} />
        <Navbar />
        <Routes>
          <Route path="/" element={<Homepage />} />
          <Route path="/forum" element={<Forum />} />
          <Route path="/visionfaqs" element={<VisionFAQs />} />
          <Route path="/search" element={<SearchResults query={searchQuery} results={searchResults} />} />
        </Routes>
        <Footer />
        <BackToTopButton />
      </div>
    </BrowserRouter>
  );
};

export default App;
