import React, { useState } from 'react';
import './FaqBubble.css';

const FaqBubble = ({ question, answer }) => {
  const [showAnswer, setShowAnswer] = useState(false);

  const handleToggle = () => {
    setShowAnswer(!showAnswer);
  };

  return (
    <div className="faq-bubble" onClick={handleToggle}>
      <div className="faq-bubble-question">
        <h3>{question}</h3>
      </div>
      {showAnswer && (
        <div className="faq-bubble-answer">
          <p>{answer}</p>
        </div>
      )}
    </div>
  );
};

export default FaqBubble;
