import React, { useState } from 'react';
import './Roadmap.css';

const Roadmap = () => {
  const [currentStep, setCurrentStep] = useState(null);

  const handleStepClick = (step) => {
    setCurrentStep(step);
    document.getElementById('ppt-container').scrollIntoView({ behavior: 'smooth' });
  };

  const steps = [
    { title: 'Step 1: Click for details', top: '25%', left: '70%' },
    { title: 'Step 2: Click for details', top: '39%', left: '2%' },
    { title: 'Step 3: Click for details', top: '51%', left: '70%' },
    { title: 'Step 4: Click for details', top: '60%', left: '2%' },
    { title: 'Step 5: Click for details', top: '70%', left: '30%' },
    { title: 'Step 6: Click for details', top: '85%', left: '35%' },
  ];

  return (
    <div className="roadmap-container">
      <div className="image-container">
        <img
          src={`${process.env.PUBLIC_URL}/Road to the independence.png`}
          alt="Road to Independence"
          className="roadmap-image"
        />
        {steps.map((step, index) => (
          <div
            key={index}
            className="hotspot"
            style={{ top: step.top, left: step.left }}
            onClick={() => handleStepClick(index)}
            title={`Click to see details about ${step.title}`}
          >
            {step.title}
          </div>
        ))}
      </div>
      <div id="ppt-container" className="ppt-container">
        {currentStep !== null && (
          <div className="ppt-content">
            <h2>{steps[currentStep].title}</h2>
            <p>Here is the detailed content for {steps[currentStep].title}.</p>
            {/* Add more content for each step here */}
          </div>
        )}
      </div>
    </div>
  );
};

export default Roadmap;
