import React, { useState } from 'react';
import './SearchResultCard.css';

const SearchResultCard = ({ result }) => {
  const [expanded, setExpanded] = useState(false);

  const toggleExpanded = () => {
    setExpanded(!expanded);
  };

  return (
    <div className={`search-result-card ${expanded ? 'expanded' : ''}`}>
      <div className="result-image">
        <img src={result.image} alt={result.title} />
      </div>
      <div className="result-details">
        <h3>{result.title}</h3>
        <div className="result-tags">
          {result.tags.map((tag, index) => (
            <span key={index} className="tag">{tag}</span>
          ))}
        </div>
        <p>{result.description}</p>
        {result.video && (
          <div className="media">
            <video controls>
              <source src={result.video} type="video/mp4" />
              Your browser does not support the video tag.
            </video>
          </div>
        )}
        {result.audio && (
          <div className="media">
            <audio controls>
              <source src={result.audio} type="audio/mpeg" />
              Your browser does not support the audio tag.
            </audio>
          </div>
        )}
        {result.map && (
          <div className="media">
            {/* 显示地图的相关内容，例如使用地图API或者图片展示 */}
            <img src={result.map} alt="Map" />
          </div>
        )}
        {result.moreInfo && (
          <div className="more-info">
            {expanded ? (
              <div>
                <p>{result.moreInfo}</p>
                {/* 在此处添加更多内容，如更多图片、链接等 */}
              </div>
            ) : (
              <button onClick={toggleExpanded}>Click for more information</button>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default SearchResultCard;
