import React, { useState } from 'react';

const Header = () => {
  const [fontSize, setFontSize] = useState(16); // 初始字体大小为 16px

  const increaseFontSize = () => {
    setFontSize(fontSize + 3); // 增加字体大小
  };

  const decreaseFontSize = () => {
    setFontSize(fontSize - 3); // 减小字体大小
  };

  return (
    <header className="header">
      <div className="top-right-search"> {/* 新增的容器 */}
      </div>
      <a href="#main-content" className="skip-to-content">Skip to content</a>
      <div className="title-container">
        <h1 className="title">
          Road to Independence
          <span className="braille">
            ⠠⠗⠕⠁⠙ ⠞⠕ ⠊⠝⠙⠑⠏⠑⠝⠙⠑⠝⠉⠑
          </span>
        </h1>
        <p className="tagline">
          A journey beyond what you SEE
          <span className="braille">
            ⠠⠁ ⠚⠕⠥⠗⠝⠑⠽ ⠃⠑⠽⠕⠝⠙ ⠺⠓⠁⠞ ⠽⠕⠥ ⠎⠑⠑
          </span>
        </p>
      </div>
      <div className="controls-container">
        <button onClick={increaseFontSize} className="font-control" id="increaseFontButton">A+</button>
        <button onClick={decreaseFontSize} className="font-control" id="decreaseFontButton">A-</button>
      </div>
      <style>
        {`
          .header {
            display: flex;
            justify-content: space-between;
            padding: 20px; /* 增大整体 padding */
            background-color: #f0f0f0; /* 示例背景色 */
            height: auto; /* 自动高度 */
          }

          .title-container {
            align-items: flex-start; /* 左对齐 */
            margin-left: 5px;
            margin-bottom: 10px; /* 调整标题和标语的间距 */
          }

          .title {
            font-size: ${fontSize}px; /* 使用动态的 fontSize */
            font-weight: bold;
            text-align: left; /* 文字左对齐 */
            margin-bottom: 5px; /* 调整标题和标语的间距 */
          }

          .tagline {
            font-size: ${fontSize - 12}px; /* 使用动态的 fontSize */
            color: #333; /* 调整标语颜色 */
            text-align: left; /* 文字左对齐 */
            margin-bottom: 20px; /* 调整标语与下面内容的间距 */
          }

          .braille {
            font-size: 14px; /* 增大盲文字体大小 */
            color: #888;
            margin-left: 5px;
          }

          .controls-container {
            display: flex;
            align-items: flex-end;
          }

          .skip-to-content {
            font-size: 24px; /* 增大 "Skip to content" 字体大小 */
          }

          .font-control {
            font-size: 24px; /* 增大字体控制按钮的字体大小 */
            padding: 8px 12px; /* 调整字体控制按钮的内边距 */
            margin-right: 10px;
            background-color: #4CAF50; /* 示例背景色 */
            color: white; /* 示例字体颜色 */
            border: none;
            cursor: pointer;
            border-radius: 4px;
            transition: background-color 0.3s;
          }

          .font-control:hover {
            background-color: #45a049; /* 悬停时的背景色 */
          }
        `}
      </style>
    </header>
  );
};

export default Header;
