import React, { useState, useEffect } from 'react';
import { useHighContrast } from '../../../components/HighContrastContext';
import './Encourage.css';

const Encourage = () => {
  const { isHighContrast } = useHighContrast();
  const [bubbles, setBubbles] = useState([
    {
      id: 1,
      content: "If people say how can you live with blindness.",
      poppedContent: "SAY, 'I lived, bitch.' Blindness is just a part of your life. Keep moving forward. Make people's mouths shout and watch you become the GOAT.",
      popped: false,
      style: {
        top: Math.random() * 70 + '%',
        left: Math.random() * 70 + '%',
        transform: 'translate(-50%, -50%)',
      }
    },
    // 可以添加更多的气泡对象
  ]);

  useEffect(() => {
    const updateBubblePositions = () => {
      const updatedBubbles = bubbles.map(bubble => ({
        ...bubble,
        style: {
          ...bubble.style,
          top: Math.random() * 70 + '%',
          left: Math.random() * 70 + '%',
        }
      }));
      setBubbles(updatedBubbles);
    };

    const interval = setInterval(updateBubblePositions, 3000); // 每隔3秒更新气泡位置
    return () => clearInterval(interval);
  }, [bubbles]);

  const handleClick = (id) => {
    const updatedBubbles = bubbles.map(bubble =>
      bubble.id === id ? { ...bubble, popped: true } : bubble
    );
    setBubbles(updatedBubbles);
    playBurstSound(); // 播放爆破音效
  };

  const playBurstSound = () => {
    const audio = new Audio(`${process.env.PUBLIC_URL}/burst.mp3`);
    audio.play();
  };

  return (
    <div className={`encourage-page ${isHighContrast ? 'high-contrast' : ''}`}>
      <h1 className="page-title">Click on the bubble to break the nonsense</h1>
      <div className="bubble-container">
        {bubbles.map(bubble => (
          <div key={bubble.id} className={`bubble ${bubble.popped ? 'popped' : ''}`} style={bubble.style}>
            <div className="bubble-overlay" onClick={() => handleClick(bubble.id)}></div>
            <div className="bubble-content">
              <p>{bubble.popped ? bubble.poppedContent : bubble.content}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Encourage;
