import React, { useState } from 'react';
import './Roadmap.css';

const Roadmap = () => {
  const [zoomedArea, setZoomedArea] = useState(null);

  const handleZoomClick = (areaId) => {
    setZoomedArea(areaId === zoomedArea ? null : areaId);
  };

  return (
    <div className="roadmap-container">
      <div className="zoomable-image">
        <div className="image-container">
          <img
            src={`${process.env.PUBLIC_URL}/Road to the independence.png`}
            alt="Road to Independence"
            onClick={() => handleZoomClick(zoomedArea ? null : 'full')}
            className={zoomedArea ? 'small-image' : 'full-image'}
          />
          {zoomedArea && (
            <div className="zoomed-area">
              {/* 显示放大的部分，具体内容根据需要添加 */}
            </div>
          )}
        </div>
        <div className="thumbnail">
          <img
            src={`${process.env.PUBLIC_URL}/Road to the independence.png`}
            alt="Thumbnail"
            onClick={() => handleZoomClick(zoomedArea ? null : 'full')}
          />
          <div className="zoom-icon">
            <i className="fa fa-search-plus" onClick={() => handleZoomClick(zoomedArea ? null : 'full')}></i>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Roadmap;
