import React from 'react';
import './Service.css'; // Your CSS file for styling

const Service = () => {
  // Define your options for each category
  const locations = [
    { text: 'Manhattan', image: 'https://assets.simpleviewinc.com/simpleview/image/upload/c_fill,h_391,q_75,w_588/v1/clients/newyorkstate/mariograzianophotography_Instagram_11d7bfa9-c11f-4feb-a919-13d479b99fd2.jpg' },
    { text: 'Brooklyn', image: 'https://images.ctfassets.net/1aemqu6a6t65/68nkexvLlGiTxvxFvzoELk/68ee51265baad76b8d7f5ae8cd99bf2c/brooklyn-bridge-sunset-julienne-schaer.jpg' },
    { text: 'Bronx', image: 'location2.jpg' },
    { text: 'Staten Island', image: 'location2.jpg' },
    { text: 'Queens', image: 'location2.jpg' },
    { text: 'Others', image: 'location2.jpg' },
  ];

  const ageRanges = [
    { text: 'Under 10', image: 'age1.jpg' },
    { text: '11-21', image: 'age2.jpg' },
    { text: '22-54', image: 'age2.jpg' },
    { text: 'Over 55', image: 'age2.jpg' },
    { text: 'All Ages', image: 'age2.jpg' },
  ];

  const serviceTypes = [
    { text: 'Education & Training', image: 'service1.jpg' },
    { text: 'Medical & Rehabilitation Services', image: 'service2.jpg' },
    { text: 'Social & Living Support', image: 'service2.jpg' },
    { text: 'Employment & Career Development', image: 'service2.jpg' },
    { text: 'Technology & Assistive Devices', image: 'service2.jpg' },
  ];

  // Function to render each row of options
  const renderOptions = (options) => (
    <div className="options-row">
      {options.map((option, index) => (
        <div key={index} className="option-block">
          <img src={option.image} alt={option.text} className="option-image" />
          <p className="option-text">{option.text}</p>
        </div>
      ))}
    </div>
  );

  return (
    <div className="service-page">
      <h2>Search Options</h2>
      <div className="category">
        <h3>Locations</h3>
        {renderOptions(locations)}
      </div>
      <div className="category">
        <h3>Age ranges</h3>
        {renderOptions(ageRanges)}
      </div>
      <div className="category">
        <h3>Service types</h3>
        {renderOptions(serviceTypes)}
      </div>
    </div>
  );
}

export default Service;
