import React, { useState } from 'react';
import Modal from './Modal';
import './Homepage.css';

const sections = [
  { id: 1, title: 'Diagnosis', content: 'Content for Diagnosis', thumbnail: 'diagnosis.jpg' },
  { id: 2, title: 'Counselling', content: 'Content for Counselling', thumbnail: 'counselling.jpg' },
  { id: 3, title: 'Goals', content: 'Content for Goals', thumbnail: 'goals.jpg' },
  { id: 4, title: 'Connection', content: 'Content for Connection', thumbnail: 'connection.jpg' },
  { id: 5, title: 'Participate', content: 'Content for Participate', thumbnail: 'participate.jpg' },
  { id: 6, title: 'Empower', content: 'Content for Empower', thumbnail: 'empower.jpg' },
];

const Homepage = () => {
  const [currentSection, setCurrentSection] = useState(null);

  const handleSectionClick = (section) => {
    setCurrentSection(section);
  };

  const closeModal = () => {
    setCurrentSection(null);
  };

  return (
    <div className="roadmap">
      <div className="content-area">
        <div className="container">
          <img className="gif" src="/Cane walking.gif" alt="Blind person walking with a cane" />
          <div className="overlay-text">
            <p>How do we survive with impaired vision?</p>
          </div>
        </div>
      </div>

      <div id="Prevalence">
        <h2>Keep this question in mind. Let's begin our journey</h2>
      </div>

      {sections.map((section) => (
        <div key={section.id} className="roadmap-section" onClick={() => handleSectionClick(section)}>
          <img src={require(`/images/${section.thumbnail}`).default} alt={section.title} />
          <div className="section-title">{section.title}</div>
        </div>
      ))}

      {currentSection && (
        <Modal>
          <div className="modal-content">
            <h2>{currentSection.title}</h2>
            <p>{currentSection.content}</p>
            <button onClick={closeModal}>Close</button>
          </div>
        </Modal>
      )}
    </div>
  );
};

export default Homepage;
