import React, { useState } from 'react';
import './Roadmap.css';

const Roadmap = () => {
  const [currentStep, setCurrentStep] = useState(null);

  const handleStepClick = (step) => {
    setCurrentStep(step);
    document.getElementById('ppt-container').scrollIntoView({ behavior: 'smooth' });
  };

  const steps = [
    'Step 1: Initial Contact',
    'Step 2: Assessment',
    'Step 3: Planning',
    'Step 4: Training',
    'Step 5: Implementation',
    'Step 6: Follow-up'
  ];

  return (
    <div className="roadmap-container">
      <div className="image-container">
        <img
          src={`${process.env.PUBLIC_URL}/Road to the independence.png`}
          alt="Road to Independence"
          className="roadmap-image"
        />
        {steps.map((step, index) => (
          <div
            key={index}
            className="hotspot"
            style={{ top: `${10 + index * 15}%`, left: '10%' }}
            onClick={() => handleStepClick(index)}
            title={`Click to see details about ${step}`}
          >
            {step}
          </div>
        ))}
      </div>
      <div id="ppt-container" className="ppt-container">
        {currentStep !== null && (
          <div className="ppt-content">
            <h2>{steps[currentStep]}</h2>
            <p>Here is the detailed content for {steps[currentStep]}.</p>
            {/* Add more content for each step here */}
          </div>
        )}
      </div>
    </div>
  );
};

export default Roadmap;
