import React, { useEffect } from 'react';
import './Header.css';

const Header = () => {
  useEffect(() => {
    function increaseSizeBy3px() {
      console.log("Increasing font size by 3px...");
      changeFontSizeByAmount(3);
    }

    function decreaseSizeBy3px() {
      console.log("Decreasing font size by 3px...");
      changeFontSizeByAmount(-3);
    }

    function changeFontSizeByAmount(amount) {
      console.log("Changing font size by", amount, "px...");
      var elements = document.querySelectorAll('p, h1, h2, h3, h4, h5, h6');
      elements.forEach(function (element) {
        var fontSize = window.getComputedStyle(element, null).getPropertyValue('font-size');
        var currentFontSize = parseInt(fontSize);
        element.style.fontSize = (currentFontSize + amount) + "px";
      });
    }

    document.getElementById('increaseFontButton').addEventListener('click', increaseSizeBy3px);
    document.getElementById('decreaseFontButton').addEventListener('click', decreaseSizeBy3px);


    return () => {
      document.getElementById('increaseFontButton').removeEventListener('click', increaseSizeBy3px);
      document.getElementById('decreaseFontButton').removeEventListener('click', decreaseSizeBy3px);
    };
  }, []);

  return (
    <div id="header-top">
      <div id="logo">
        <img src="logo.png" alt="Logo" />
      </div>
      <div id="tagline">
        Journey of visually impaired adults - A journey that you might never SEE
      </div>
      <div id="fontControls">
        <input type="button" value="Increase Font Size" id="increaseFontButton" />
        <input type="button" value="Decrease Font Size" id="decreaseFontButton" />
      </div>
    </div>
  );
};

export default Header;
