import React, { useState, useRef } from 'react';
import './Service.css'; // Your CSS file for styling
import SearchResults from './SearchResults';

const Service = () => {
  // Define your options for each category
  const locations = [
    { id: 1, text: 'Manhattan', image: 'https://assets.simpleviewinc.com/simpleview/image/upload/c_fill,h_391,q_75,w_588/v1/clients/newyorkstate/mariograzianophotography_Instagram_11d7bfa9-c11f-4feb-a919-13d479b99fd2.jpg' },
    { id: 2, text: 'Brooklyn', image: 'https://images.ctfassets.net/1aemqu6a6t65/68nkexvLlGiTxvxFvzoELk/68ee51265baad76b8d7f5ae8cd99bf2c/brooklyn-bridge-sunset-julienne-schaer.jpg' },
    { id: 3, text: 'Bronx', image: 'https://static01.nyt.com/images/2018/04/03/nyregion/03bxjail1/merlin_136305294_3f04a5b5-4fad-4bf6-bc28-0f9a300b3d82-articleLarge.jpg?quality=75&auto=webp&disable=upscale' },
    { id: 4, text: 'Staten Island', image: 'https://assets3.thrillist.com/v1/image/1762889/1200x600/scale;;webp=auto;jpeg_quality=85.jpg' },
    { id: 5, text: 'Queens', image: 'https://suitcaseandheels.com/wp-content/uploads/2023/02/long-island-city.jpeg' },
    { id: 6, text: 'Others/At Home', image: '/Others.png' },
  ];

  const ageRanges = [
    { id: 1, text: 'Under 10', image: 'https://www.hhs.texas.gov/sites/default/files/styles/large/public/assets/services/disability/blind-visually-impaired/blind-childrens-program.jpg?itok=7Y7mpBKX' },
    { id: 2, text: '11-21', image: 'https://media.istockphoto.com/id/1334509954/photo/acessible-college-diversity.jpg?s=612x612&w=0&k=20&c=p5wMnHJspsYZzYyG8skxhKIXh1dMVQ6845mj8iF_zAs=' },
    { id: 3, text: '22-54', image: 'https://www.perkins.org/wp-content/uploads/2021/10/248A1558-Edit-1024x683.jpg' },
    { id: 4, text: 'Over 55', image: 'https://www.homecareassistancetampabay.com/wp-content/uploads/Blind-Senior.jpg' },
    { id: 5, text: 'All Ages', image: 'https://aphconnectcenter.org/wp-content/uploads/sites/5/SupportGroups.jpg' },
  ];

  const serviceTypes = [
    { id: 1, text: 'Education & Training', image: 'https://www.perkins.org/wp-content/uploads/2021/06/Braille_int_video_THUMBNAIL-scaled.jpeg' },
    { id: 2, text: 'Medical & Rehabilitation Services', image: 'https://www.salusuhealth.com/_files/images/04112017_su-tei_080.jpg' },
    { id: 3, text: 'Social & Living Support', image: 'https://chicagolighthouse.org/wp-content/uploads/2015/10/DSC_0081-1024x678.jpg' },
    { id: 4, text: 'Employment & Career Development', image: 'https://vi.ie/wp-content/uploads/2023/05/Employment-LAN.jpg' },
    { id: 5, text: 'Technology & Assistive Devices', image: 'https://us.optelec.com/binaries/content/gallery/optelec-nl/landingspagina/loep-compact-hd.jpg' },
  ];

  const [selectedLocations, setSelectedLocations] = useState(null); // Only one selection allowed
  const [selectedAgeRange, setSelectedAgeRange] = useState(null); // Only one selection allowed
  const [selectedServiceType, setSelectedServiceType] = useState(null); // Only one selection allowed
  const [searchResults, setSearchResults] = useState(null); // State to hold search results

  const searchResultsRef = useRef(null); // Ref to hold reference of search results element

  // Function to handle click on location option
  const handleLocationClick = (id) => {
    setSelectedLocations(id === selectedLocations ? null : id);
  };

  // Function to handle click on age range option
  const handleAgeRangeClick = (id) => {
    setSelectedAgeRange(id === selectedAgeRange ? null : id);
  };

  // Function to handle click on service type option
  const handleServiceTypeClick = (id) => {
    setSelectedServiceType(id === selectedServiceType ? null : id);
  };

  // Function to scroll to search results
  const scrollToSearchResults = () => {
    const searchResultsElement = document.getElementById('search-results');
    if (searchResultsElement) {
      searchResultsElement.scrollIntoView({ behavior: 'smooth' });
    }
  };

  // Function to handle GO button click
  const handleGoButtonClick = () => {
    // Check if all selections are made
    if (selectedLocations !== null && selectedAgeRange !== null && selectedServiceType !== null) {
      // For demo purposes, set a mock search result
      const results = [
        { 
          id: 1, 
          title: 'Schermerhorn Program', 
          description: 'The Schermerhorn Program has a 190-year history of serving students with blindness and visual impairments. It offers diverse educational services to meet the needs of children who are legally blind, from the ages of 5 to 21.', 
          image: 'https://3.files.edl.io/d7d1/23/08/21/190246-d4a29c1d-6463-4e84-97a6-c979c98b4e08.jpg', 
          link: 'https://www.nyise.org/apps/pages/index.jsp?uREC_ID=409369&type=d&pREC_ID=892615' 
        }
      ];
      setSearchResults(results);

      // Scroll to search results
      scrollToSearchResults();
    }
  };

  const allSelectionsMade = selectedLocations !== null && selectedAgeRange !== null && selectedServiceType !== null;

  return (
    <div className="service-page">
      <h2>Search Options</h2>
      <div className="category">
        <h3>Locations</h3>
        <div className="options-row">
          {locations.map((location) => (
            <div
              key={location.id}
              className={`option-block ${selectedLocations === location.id ? 'selected' : ''}`}
              onClick={() => handleLocationClick(location.id)}
            >
              <img src={location.image} alt={location.text} className="option-image" />
              <p className="option-text">{location.text}</p>
            </div>
          ))}
        </div>
      </div>
      <div className="category">
        <h3>Age ranges</h3>
        <div className="options-row">
          {ageRanges.map((ageRange) => (
            <div
              key={ageRange.id}
              className={`option-block ${selectedAgeRange === ageRange.id ? 'selected' : ''}`}
              onClick={() => handleAgeRangeClick(ageRange.id)}
            >
              <img src={ageRange.image} alt={ageRange.text} className="option-image" />
              <p className="option-text">{ageRange.text}</p>
            </div>
          ))}
        </div>
      </div>
      <div className="category">
        <h3>Service types</h3>
        <div className="options-row">
          {serviceTypes.map((serviceType) => (
            <div
              key={serviceType.id}
              className={`option-block ${selectedServiceType === serviceType.id ? 'selected' : ''}`}
              onClick={() => handleServiceTypeClick(serviceType.id)}
            >
              <img src={serviceType.image} alt={serviceType.text} className="option-image" />
              <p className="option-text">{serviceType.text}</p>
            </div>
          ))}
        </div>
      </div>
      {allSelectionsMade && (
        <button className="go-button" onClick={handleGoButtonClick}>
          GO
        </button>
      )}
      <SearchResults results={searchResults} />
    </div>
  );
};

export default Service;
