import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faProjectDiagram, faDraftingCompass, faLink } from '@fortawesome/free-solid-svg-icons';
import './About.css';

const About = () => {

  const openInNewTab = (url) => {
    const newWindow = window.open(url, '_blank', 'noopener,noreferrer');
    if (newWindow) newWindow.opener = null;
  };

  const openAboutMe = () => {
    openInNewTab('https://xanderxuan.wordpress.com');
  };

  const openTheProject = () => {
    openInNewTab('https://medium.com/@tianhe1019/a-digital-initiative-for-the-visually-impaired-in-new-york-3d405de5a4e5');
  };

  return (
    <div className="about-overview">
      <div className="section about-me">
      <h2 className="section-title clickable">
        About Me <span className="click-for-details">(click for details)</span>
      </h2>
      <div className="section-content">
        <div className="up">
          <img className="profile-photo" src={`${process.env.PUBLIC_URL}/profile-photo.jpg`} alt="Profile" />
          <p className="name">Your Name</p>
        </div>
        <div className="down">
          <p className="description">
            Your introduction text goes here. Lorem ipsum dolor sit amet, consectetur adipiscing elit.
            Phasellus sit amet quam odio. Integer nec odio. Praesent libero. Sed cursus ante dapibus diam.
          </p>
        </div>
      </div>
    </div>

      <div className="section about-project">
        <h2 className="section-title clickable" onClick={openTheProject}>
          About the Project <span className="click-for-details" style={{ color: 'blue' }}> (click for details)</span>
        </h2>
        <div className="section-content">
          <div className="item">
            <FontAwesomeIcon icon={faProjectDiagram} size="3x" />
            <p>Introduction</p>
          </div>
          <div className="item">
            <FontAwesomeIcon icon={faDraftingCompass} size="3x" />
            <p>Blueprint</p>
          </div>
          <div className="item">
            <FontAwesomeIcon icon={faLink} size="3x" />
            <p>Reference</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default About;
