import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';// 导入路由历史对象
import './MiniSearch.css';
import MiniSearch from 'minisearch';
import keywordLists from '../config/keywordLists'; // 导入关键词列表

const MiniSearchComponent = ({ data }) => {
  const [query, setQuery] = useState('');
  const miniSearch = new MiniSearch({ fields: ['title', 'link'] });
  const navigate  = useNavigate(); // 获取路由历史对象

  useEffect(() => {
    miniSearch.addAll(data);
  }, [data]);

  const handleSearch = (e) => {
    const { value } = e.target;
    setQuery(value);

    const results = miniSearch.search(value);
    if (results.length > 0) {
      // 获取第一个搜索结果的链接，并跳转
      const firstResult = results[0];
      const page = getPageFromKeyword(firstResult.title.toLowerCase()); // 根据标题获取页面
      history.push(page); // 使用路由跳转到页面
    }
  };

  const getPageFromKeyword = (keyword) => {
    // 根据关键词查找对应的页面
    for (let page in keywordLists) {
      if (keywordLists[page].includes(keyword)) {
        return `/${page}`;
      }
    }
    return '/'; // 如果未找到，默认返回首页
  };

  return (
    <div className="search-container">
      <input
        type="text"
        value={query}
        onChange={handleSearch}
        placeholder="Search..."
        className="search-input"
      />
    </div>
  );
};

export default MiniSearchComponent;
