import React, { useState } from 'react';
import './FaqPanel.css';

const FaqPanel = ({ question, answer }) => {
  const [isOpen, setIsOpen] = useState(false);

  const handleToggle = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className="faq-panel">
      <div className="faq-panel-header" onClick={handleToggle}>
        <h3>{question}</h3>
      </div>
      {isOpen && (
        <div className="faq-panel-body">
          <p>{answer}</p>
        </div>
      )}
    </div>
  );
};

export default FaqPanel;
