import React, { useState } from 'react';
import './Service.css'; // Your CSS file for styling

const Service = () => {
  // Define your options for each category
  const locations = [
    { id: 1, text: 'Manhattan', image: 'https://assets.simpleviewinc.com/simpleview/image/upload/c_fill,h_391,q_75,w_588/v1/clients/newyorkstate/mariograzianophotography_Instagram_11d7bfa9-c11f-4feb-a919-13d479b99fd2.jpg' },
    { id: 2, text: 'Brooklyn', image: 'https://images.ctfassets.net/1aemqu6a6t65/68nkexvLlGiTxvxFvzoELk/68ee51265baad76b8d7f5ae8cd99bf2c/brooklyn-bridge-sunset-julienne-schaer.jpg' },
    { id: 3, text: 'Bronx', image: 'location2.jpg' },
    { id: 4, text: 'Staten Island', image: 'location2.jpg' },
    { id: 5, text: 'Queens', image: 'location2.jpg' },
    { id: 6, text: 'Others', image: 'location2.jpg' },
  ];

  const ageRanges = [
    { id: 1, text: 'Under 10', image: 'age1.jpg' },
    { id: 2, text: '11-21', image: 'age2.jpg' },
    { id: 3, text: '22-54', image: 'age2.jpg' },
    { id: 4, text: 'Over 55', image: 'age2.jpg' },
    { id: 5, text: 'All Ages', image: 'age2.jpg' },
  ];

  const serviceTypes = [
    { id: 1, text: 'Education & Training', image: 'service1.jpg' },
    { id: 2, text: 'Medical & Rehabilitation Services', image: 'service2.jpg' },
    { id: 3, text: 'Social & Living Support', image: 'service2.jpg' },
    { id: 4, text: 'Employment & Career Development', image: 'service2.jpg' },
    { id: 5, text: 'Technology & Assistive Devices', image: 'service2.jpg' },
  ];

  // State to track selected options
  const [selectedLocations, setSelectedLocations] = useState([]);
  const [selectedAgeRange, setSelectedAgeRange] = useState(null); // Only one selection allowed
  const [selectedServiceTypes, setSelectedServiceTypes] = useState(new Set()); // Multiple selections allowed

  // Function to handle click on location option
  const handleLocationClick = (id) => {
    const index = selectedLocations.indexOf(id);
    if (index === -1) {
      setSelectedLocations([...selectedLocations, id]);
    } else {
      setSelectedLocations(selectedLocations.filter(item => item !== id));
    }
  };

  // Function to handle click on age range option
  const handleAgeRangeClick = (id) => {
    setSelectedAgeRange(id === selectedAgeRange ? null : id);
  };

  // Function to handle click on service type option
  const handleServiceTypeClick = (id) => {
    const updatedSet = new Set(selectedServiceTypes);
    if (updatedSet.has(id)) {
      updatedSet.delete(id);
    } else {
      updatedSet.add(id);
    }
    setSelectedServiceTypes(updatedSet);
  };

  // Function to render each row of options
  const renderOptions = (options, handleClick, selectedItems) => (
    <div className="options-row">
      {options.map(option => (
        <div
          key={option.id}
          className={`option-block ${selectedItems.includes(option.id) ? 'selected' : ''}`}
          onClick={() => handleClick(option.id)}
        >
          <img src={option.image} alt={option.text} className="option-image" />
          <p className="option-text">{option.text}</p>
        </div>
      ))}
    </div>
  );

  return (
    <div className="service-page">
      <h2>Search Options</h2>
      <div className="category">
        <h3>Locations</h3>
        {renderOptions(locations, handleLocationClick, selectedLocations)}
      </div>
      <div className="category">
        <h3>Age ranges</h3>
        {renderOptions(ageRanges, handleAgeRangeClick, selectedAgeRange ? [selectedAgeRange] : [])}
      </div>
      <div className="category">
        <h3>Service types</h3>
        {renderOptions(serviceTypes, handleServiceTypeClick, [...selectedServiceTypes])}
      </div>
    </div>
  );
}

export default Service;
