import React, { useState } from 'react';
import OptionCard from './OptionCard';
import SearchResults from './SearchResults';
import './Service.css';

const options = {
  locations: [
    { text: 'Manhattan', image: 'https://assets.simpleviewinc.com/simpleview/image/upload/c_fill,h_391,q_75,w_588/v1/clients/newyorkstate/mariograzianophotography_Instagram_11d7bfa9-c11f-4feb-a919-13d479b99fd2.jpg' },
    { text: 'Brooklyn', image: 'https://images.ctfassets.net/1aemqu6a6t65/68nkexvLlGiTxvxFvzoELk/68ee51265baad76b8d7f5ae8cd99bf2c/brooklyn-bridge-sunset-julienne-schaer.jpg' },
    { text: 'Bronx', image: 'location2.jpg' },
    { text: 'Staten Island', image: 'location2.jpg' },
    { text: 'Queens', image: 'location2.jpg' },
    { text: 'Others', image: 'location2.jpg' },
  ],
  ageRanges: [
    { text: 'Under 10', image: 'age1.jpg' },
    { text: '11-21', image: 'age2.jpg' },
    { text: '22-54', image: 'age2.jpg' },
    { text: 'Over 55', image: 'age2.jpg' },
    { text: 'All Ages', image: 'age2.jpg' },
  ],
  serviceTypes: [
    { text: 'Education & Training', image: 'service1.jpg' },
    { text: 'Medical & Rehabilitation Services', image: 'service2.jpg' },
    { text: 'Social & Living Support', image: 'service2.jpg' },
    { text: 'Employment & Career Development', image: 'service2.jpg' },
    { text: 'Technology & Assistive Devices', image: 'service2.jpg' },
    // Add more service types
  ],
};

const Service = () => {
  const [selectedLocation, setSelectedLocation] = useState(null);
  const [selectedAgeRange, setSelectedAgeRange] = useState(null);
  const [selectedServiceTypes, setSelectedServiceTypes] = useState([]);

  const handleSelectServiceType = (serviceType) => {
    setSelectedServiceTypes((prev) =>
      prev.includes(serviceType)
        ? prev.filter((st) => st !== serviceType)
        : [...prev, serviceType]
    );
  };

  const handleSearch = () => {
    // Implement search logic
  };

  return (
    <div className="search-page">
      <div className="options-section">
        <h2>Locations</h2>
        <div className="options-grid">
          {options.locations.map((option) => (
            <OptionCard
              key={option.text}
              option={option}
              onSelect={setSelectedLocation}
              isSelected={selectedLocation === option}
            />
          ))}
        </div>
      </div>
      <div className="options-section">
        <h2>Age Range</h2>
        <div className="options-grid">
          {options.ageRanges.map((option) => (
            <OptionCard
              key={option.text}
              option={option}
              onSelect={setSelectedAgeRange}
              isSelected={selectedAgeRange === option}
            />
          ))}
        </div>
      </div>
      <div className="options-section">
        <h2>Service Type</h2>
        <div className="options-grid">
          {options.serviceTypes.map((option) => (
            <OptionCard
              key={option.text}
              option={option}
              onSelect={handleSelectServiceType}
              isSelected={selectedServiceTypes.includes(option)}
            />
          ))}
        </div>
      </div>
      <button className="go-button" onClick={handleSearch}>
        <i className="fas fa-search"></i> GO
      </button>
      {/* Display search results */}
      <SearchResults
        location={selectedLocation}
        ageRange={selectedAgeRange}
        serviceTypes={selectedServiceTypes}
      />
    </div>
  );
};

export default Service;
