import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faBriefcase, faBook, faProjectDiagram, faDraftingCompass, faLink } from '@fortawesome/free-solid-svg-icons';
import './About.css';

const About = () => {

  const openInNewTab = (url) => {
    const newWindow = window.open(url, '_blank', 'noopener,noreferrer');
    if (newWindow) newWindow.opener = null;
  };

  const openAboutMe = () => {
    openInNewTab('https://xanderxuan.wordpress.com');
  };

  const openTheProject = () => {
    openInNewTab('https://medium.com/@tianhe1019/a-digital-initiative-for-the-visually-impaired-in-new-york-3d405de5a4e5');
  };

  return (
    <div className="about-overview">
      <div className="section about-me">
        <h2 className="section-title clickable" onClick={openAboutMe}>
          About Me <span className="click-for-details" style={{ color: 'blue' }}> (click for details)</span>
        </h2>
        <div className="section-content">
          <div className="item">
            <FontAwesomeIcon icon={faUser} size="3x" />
            <p>Introduction</p>
          </div>
          <div className="item">
            <FontAwesomeIcon icon={faBriefcase} size="3x" />
            <p>Experience</p>
          </div>
          <div className="item">
            <FontAwesomeIcon icon={faBook} size="3x" />
            <p>Story</p>
          </div>
        </div>
      </div>

      <div className="section about-project">
        <h2 className="section-title clickable" onClick={openTheProject}>
          About the Project <span className="click-for-details" style={{ color: 'blue' }}> (click for details)</span>
        </h2>
        <div className="section-content">
          <div className="item">
            <FontAwesomeIcon icon={faProjectDiagram} size="3x" />
            <p>Introduction</p>
          </div>
          <div className="item">
            <FontAwesomeIcon icon={faDraftingCompass} size="3x" />
            <p>Blueprint</p>
          </div>
          <div className="item">
            <FontAwesomeIcon icon={faLink} size="3x" />
            <p>Reference</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default About;
