import React, { useRef, useImperativeHandle } from 'react';
import './GIF.css';

const GIF = React.forwardRef((props, ref) => {
  const gifRef = useRef(null);

  useImperativeHandle(ref, () => ({
    scrollToSection(id) {
      const element = document.getElementById(id);
      console.log('Scrolling to:', element);
      if (element) {
        element.scrollIntoView({ behavior: 'smooth' });
      }
    }
  }));

  return (
    <div className="content-area" ref={gifRef}>
      <div className="container">
        <img className="gif" src="/Cane walking.gif" alt="Blind person walking with a cane" />
        <div className="top-right-container">
          <div className="top-right-text">
            <p>Journey of visually impaired persons</p>
            <hr className="separator" />
            <p className="smaller-text">A journey that you might never SEE</p>
          </div>
        </div>
      </div>
      <div id="navigation" className="show">
        <p className="navigation-info">Click on these elements to explore the page:</p>
        <ul className="navigation-list">
          <li onClick={() => props.scrollToSection('Elements')}>Core Features</li>
          <li onClick={() => props.scrollToSection('Roadmap')}>Roadmap</li>
          <li onClick={() => props.scrollToSection('Feedback')}>Feedback</li>
        </ul>
      </div>
    </div>
  );
});

export default GIF;
