import React, { useState } from 'react';
import { useHighContrast } from '../../../components/HighContrastContext';
import './Encourage.css';

const Encourage = () => {
  const { isHighContrast } = useHighContrast();
  const [bubbles, setBubbles] = useState([
    {
      id: 1,
      content: "If people say how can you live with blindness.",
      poppedContent: "<h1 style={{color: 'red'}}>“I lived, bitch.”</h1> <h2>Blindness is just a part of your life. Keep moving forward. Make people's mouths shout and watch you become the GOAT.</h2>",
      popped: false,
      style: {
        top: '50%', // 初始位置
        left: '50%', // 初始位置
        transform: 'translate(-50%, -50%)',
      }
    },
    // 添加更多气泡对象
  ]);

  const handleMouseDown = (id, event) => {
    event.preventDefault(); // 阻止默认行为，避免选中文本
    const bubbleIndex = bubbles.findIndex(bubble => bubble.id === id);
    const updatedBubbles = [...bubbles];
    updatedBubbles[bubbleIndex] = {
      ...updatedBubbles[bubbleIndex],
      style: {
        ...updatedBubbles[bubbleIndex].style,
        initialMouseX: event.clientX,
        initialMouseY: event.clientY,
        initialTop: parseFloat(updatedBubbles[bubbleIndex].style.top),
        initialLeft: parseFloat(updatedBubbles[bubbleIndex].style.left),
        isDragging: true,
      }
    };
    setBubbles(updatedBubbles);
  };

  const handleMouseMove = (id, event) => {
    const bubbleIndex = bubbles.findIndex(bubble => bubble.id === id);
    if (bubbles[bubbleIndex].style.isDragging) {
      const deltaX = event.clientX - bubbles[bubbleIndex].style.initialMouseX;
      const deltaY = event.clientY - bubbles[bubbleIndex].style.initialMouseY;
      const newTop = bubbles[bubbleIndex].style.initialTop + deltaY;
      const newLeft = bubbles[bubbleIndex].style.initialLeft + deltaX;
      const updatedBubbles = [...bubbles];
      updatedBubbles[bubbleIndex] = {
        ...updatedBubbles[bubbleIndex],
        style: {
          ...updatedBubbles[bubbleIndex].style,
          top: `${newTop}px`,
          left: `${newLeft}px`,
        }
      };
      setBubbles(updatedBubbles);
    }
  };

  const handleMouseUp = (id) => {
    const bubbleIndex = bubbles.findIndex(bubble => bubble.id === id);
    if (bubbles[bubbleIndex].style.isDragging) {
      const updatedBubbles = [...bubbles];
      updatedBubbles[bubbleIndex] = {
        ...updatedBubbles[bubbleIndex],
        style: {
          ...updatedBubbles[bubbleIndex].style,
          isDragging: false,
        }
      };
      setBubbles(updatedBubbles);
    }
  };

  const handleClick = (id) => {
    const updatedBubbles = bubbles.map(bubble =>
      bubble.id === id ? { ...bubble, popped: !bubble.popped } : bubble
    );
    setBubbles(updatedBubbles);
    playBurstSound(); // 播放爆破音效
  };

  const playBurstSound = () => {
    const audio = new Audio(`${process.env.PUBLIC_URL}/burst.mp3`);
    audio.volume = 1; // 设置音量为一半
    audio.play();
  };

  return (
    <div className={`encourage-page ${isHighContrast ? 'high-contrast' : ''}`}>
      <div className="background-overlay"></div> {/* 添加背景蒙版 */}
      <h1 className="page-title">Click on the bubble to break the nonsense</h1>
      <div className="bubble-container">
        {bubbles.map(bubble => (
          <div
            key={bubble.id}
            className={`bubble ${bubble.popped ? 'popped' : ''}`}
            style={bubble.style}
            onClick={() => handleClick(bubble.id)}
            onMouseDown={(event) => handleMouseDown(bubble.id, event)}
            onMouseMove={(event) => handleMouseMove(bubble.id, event)}
            onMouseUp={() => handleMouseUp(bubble.id)}
          >
            <div className="bubble-overlay">
              <div className="bubble-content">
                {bubble.popped ? (
                  <p dangerouslySetInnerHTML={{ __html: bubble.poppedContent }} />
                ) : (
                  <p>{bubble.content}</p>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Encourage;
