import React from 'react';
import { useHighContrast } from '../../../components/HighContrastContext';
import './Encourage.css';

const Encourage = () => {
  const { isHighContrast } = useHighContrast();

  return (
    <div className={isHighContrast ? 'high-contrast' : ''}>
      <div className="encourage-page">
        <h1>Click on the bubble to break the nonsense</h1>
        <div className="bubble-container">
          <Bubble
            number={1}
            initialContent="If people say how can you live with blindness."
            poppedContent="SAY, 'I lived, bitch.' Blindness is just a part of your life. Keep moving forward. Make people's mouths shout and watch you become the GOAT."
          />
          {/* 添加更多的 Bubble 组件，每个都可以有自己的 initialContent 和 poppedContent */}
        </div>
      </div>
    </div>
  );
};

const Bubble = ({ number, initialContent, poppedContent }) => {
  const [popped, setPopped] = React.useState(false);

  const handleClick = () => {
    setPopped(true);
  };

  return (
    <div className="bubble-wrapper">
      {!popped ? (
        <div className="bubble" onClick={handleClick}>
          <img src="./bubble.jpg" alt="Bubble" />
          <span className="bubble-number">{number}</span> {/* 气泡编号 */}
          <p>{initialContent}</p>
        </div>
      ) : (
        <div className="burst-bubble">
          <img src="./bubble.jpg" alt="Bubble" />
          <div className="dialogue-box">
            <h3>反击内容</h3>
            <p>{poppedContent}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Encourage;
