import React, { useState } from 'react';
import './Story.css';

const people = [
  {
    name: 'Khadija Bari',
    description: 'Khadija Bari is the director of the Workforce Development Team at VISIONS.',
    gender: 'female',
    img: 'https://media.licdn.com/dms/image/C4E03AQEVDEhVhXGx8A/profile-displayphoto-shrink_200_200/0/1574971641500?e=2147483647&v=beta&t=3_ifP7XHz8PNz7Joqk3BMqtqcnrLRvW4p3DwiRh1Mxg',
    story: 'Khadija Bari has been a leader in advocating for workforce development for visually impaired individuals. She has worked tirelessly to promote accessibility and inclusion in employment opportunities.'
  },
  {
    name: 'Ann Chippetta',
    description: 'Ann Chippetta is an author, advocate, and guide dog handler.',
    gender: 'female',
    img: 'https://www.poluscenter.org/sites/default/files/styles/half/public/people/chiappetta.jpg?itok=3XodrosE',
    story: 'Ann Chippetta is known for her advocacy in promoting awareness about guide dog services and enhancing accessibility for the visually impaired community.'
  },
  {
    name: 'Pratik Patel',
    description: 'Pratik Patel is a blind technologist, who finds himself championing accessibility for fun and necessity.',
    gender: 'male',
    img: 'https://media.mstdn.social/accounts/avatars/109/423/558/890/402/666/original/40f45c5844557d44.jpg',
    story: 'Pratik Patel is a passionate advocate for accessible technology. He has contributed significantly to the development of tools that enhance accessibility for visually impaired individuals.'
  },
  {
    name: 'Jeremy Block',
    description: 'Jeremy Block is a Co-Founder of Medaptive Health, and adjunct professor at Baruch College.',
    gender: 'male',
    img: 'https://about.citiprogram.org/wp-content/uploads/2021/01/Jeremy-Block-STD.jpg',
    story: 'Jeremy Block is an innovator in healthcare technology, focusing on creating solutions that improve patient care and accessibility for all.'
  }
  // Add more people as needed
];

const Story = () => {
  const [filter, setFilter] = useState('');
  const [flippedCard, setFlippedCard] = useState(null);

  const filteredPeople = filter
    ? people.filter(person => person.gender === filter)
    : people;

  const handleCardFlip = (index) => {
    setFlippedCard(index === flippedCard ? null : index);
  };

  return (
    <div className="story-container">
      <div className="header">
        <div className="image-overlay"></div>
        <img className="background-image" src="https://media.sleepdoctor.com/images/f_auto,q_auto:eco/v1675291188/thesleepdoctor-com/blind-man/blind-man.jpg?_i=AA" alt="Background" />
        <div className="overlay">
          <h1>Personal Stories</h1>
          <p>Discover the inspiring stories of individuals from various walks of life, showcasing their unique journeys and achievements.</p>
        </div>
      </div>
      <div className="content">
        <div className="people-list">
          {filteredPeople.map((person, index) => (
            <div className="person-card" key={index}>
              <div className={`card ${index === flippedCard ? 'flipped' : ''}`}>
                <div className="front" onClick={() => handleCardFlip(index)}>
                  <div className="person-image">
                    <img src={person.img} alt={person.name} />
                  </div>
                  <h2>{person.name}</h2>
                  <p>{person.description}</p>
                  <a className="learn-more">Learn More</a>
                </div>
                <div className="back">
                  <div className="back-content">
                    <p>{person.story}</p>
                    {/* Add more details or components here */}
                  </div>
                  <a className="close-button" onClick={() => handleCardFlip(index)}>Close</a>
                </div>
              </div>
            </div>
          ))}
        </div>
        <div className="filter-options">
          <button onClick={() => setFilter('male')}>Meet Men</button>
          <button onClick={() => setFilter('female')}>Meet Women</button>
          <button onClick={() => setFilter('')}>Show All</button>
        </div>
      </div>
    </div>
  );
};

export default Story;
