import React, { useState } from 'react';
import { useHighContrast } from '../../../components/HighContrastContext';
import './Encourage.css';

const Encourage = () => {
  const { isHighContrast } = useHighContrast();
  const [bubbles, setBubbles] = useState([
    {
      number: 1,
      initialContent: "If people say how can you live with blindness.",
      poppedContent: "SAY, 'I lived, bitch.' Blindness is just a part of your life. Keep moving forward. Make people's mouths shout and watch you become the GOAT.",
      popped: false
    },
    // 可以添加更多的气泡对象
  ]);

  const handleClick = (index) => {
    const updatedBubbles = [...bubbles];
    updatedBubbles[index].popped = true;
    setBubbles(updatedBubbles);
    playBurstSound(); // 播放爆破音效
  };

  const playBurstSound = () => {
    const audio = new Audio(`${process.env.PUBLIC_URL}/burst.mp3`);
    audio.play();
  };

  return (
    <div className={isHighContrast ? 'high-contrast' : ''}>
      <div className="encourage-page">
        <h1>Click on the bubble to break the nonsense</h1>
        <div className="bubble-container">
          {bubbles.map((bubble, index) => (
            <div key={index} className="bubble-wrapper">
              {!bubble.popped ? (
                <div className="bubble" onClick={() => handleClick(index)}>
                  <img src={`${process.env.PUBLIC_URL}/bubble.jpg`} alt="Bubble" />
                  <span className="bubble-number">{bubble.number}</span> {/* 气泡编号 */}
                  <p>{bubble.initialContent}</p>
                </div>
              ) : (
                <div className="burst-bubble">
                  <img src={`${process.env.PUBLIC_URL}/bubble.jpg`} alt="Bubble" />
                  <div className="dialogue-box">
                    <h3>反击内容</h3>
                    <p>{bubble.poppedContent}</p>
                  </div>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Encourage;
