import React, { useState } from 'react';
import './Encourage.css'; // 样式文件
import burstSound from '"C:/Users/FeNis/my-interactive-website/public/burst.mp3"'; // 爆破音效文件

const Encourage = ({ number, initialContent, poppedContent }) => {
  const [popped, setPopped] = useState(false);

  const handleClick = () => {
    setPopped(true);
    playBurstSound(); // 播放爆破音效
  };

  const playBurstSound = () => {
    const audio = new Audio(burstSound);
    audio.play();
  };

  return (
    <div className="bubble-wrapper">
      {!popped ? (
        <div className="bubble" onClick={handleClick}>
          <img src="/bubble.jpg" alt="Bubble" />
          <span className="bubble-number">{number}</span> {/* 气泡编号 */}
          <p>{initialContent}</p>
        </div>
      ) : (
        <div className="burst-bubble">
          <img src="/bubble.jpg" alt="Bubble" />
          <div className="dialogue-box">
            <h3>反击内容</h3>
            <p>{poppedContent}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Encourage;
