import React from 'react';
import './GIF.css';

const GIF = () => {
  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <div className="content-area">
      <div className="container">
        <img className="gif" src="/Cane walking.gif" alt="Blind person walking with a cane" />
        <div className="top-right-container">
          <div className="top-right-text">
            <p>Journey of visually impaired persons</p>
            <hr className="separator" />
            <p className="smaller-text">A journey that you might never SEE</p>
          </div>
        </div>
      </div>
      <div id="Prevalence">
        <h2>Let's begin our journey</h2>
        <div className="page-navigation">
          <ul>
            <li onClick={() => scrollToSection('introduction')}>Introduction</li>
            <li onClick={() => scrollToSection('core-features')}>Core Features</li>
            <li onClick={() => scrollToSection('roadmap')}>Roadmap</li>
          </ul>
        </div>
      </div>

      <section id="introduction" className="section">
        <h2>Introduction</h2>
        <p>Welcome to the 'Road to Independence'. Explore the journey and resources available for visually impaired individuals.</p>
      </section>

      <section id="core-features" className="section">
        <h2>Core Features</h2>
        <p>Explore the core features of our platform designed to assist visually impaired individuals.</p>
      </section>

      <section id="roadmap" className="section">
        <h2>Roadmap</h2>
        <p>Check out our roadmap for future developments and enhancements.</p>
      </section>
    </div>
  );
};

export default GIF;
