import React, { useState } from 'react';
import './Story.css';

const people = [
  {
    name: 'Khadija Bari',
    description: 'Khadija Bari is the director of the Workforce Development Team at VISIONS/Services for the Blind & Visually Impaired',
    gender: 'female',
    img: 'https://media.licdn.com/dms/image/C4E03AQEVDEhVhXGx8A/profile-displayphoto-shrink_200_200/0/1574971641500?e=2147483647&v=beta&t=3_ifP7XHz8PNz7Joqk3BMqtqcnrLRvW4p3DwiRh1Mxg',
    story: 'Khadija Bari has been a leading figure in advocating for the rights of the visually impaired, spearheading numerous initiatives that have significantly improved the lives of many.'
  },
  {
    name: 'Ann Chippetta',
    description: 'Ann Chippetta is an author, advocate, and guide dog handler.',
    gender: 'female',
    img: 'https://www.poluscenter.org/sites/default/files/styles/half/public/people/chiappetta.jpg?itok=3XodrosE',
    story: 'Ann Chippetta has written several books and articles that provide deep insights into the experiences of living with blindness. Her advocacy work has helped raise awareness and drive change.'
  },
  {
    name: 'Pratik Patel',
    description: 'Pratik Patel is a blind technologist, who finds himself championing accessibility for fun and necessity.',
    gender: 'male',
    img: 'https://media.mstdn.social/accounts/avatars/109/423/558/890/402/666/original/40f45c5844557d44.jpg',
    story: 'Pratik Patel is at the forefront of technology and accessibility, working tirelessly to ensure that digital environments are accessible to all.'
  },
  {
    name: 'Jeremy Block',
    description: 'Jeremy Block is a Co-Founder of Medaptive Health, and adjunct professor at Baruch College.',
    gender: 'male',
    img: 'https://about.citiprogram.org/wp-content/uploads/2021/01/Jeremy-Block-STD.jpg',
    story: 'Jeremy Block combines his expertise in health and education to innovate solutions that improve patient care and learning outcomes.'
  }
  // Add more people as needed
];

const Story = () => {
  const [filter, setFilter] = useState('');
  const [flipped, setFlipped] = useState(Array(people.length).fill(false));

  const handleFlip = (index) => {
    const newFlipped = [...flipped];
    newFlipped[index] = !newFlipped[index];
    setFlipped(newFlipped);
  };

  const filteredPeople = filter
    ? people.filter(person => person.gender === filter)
    : people;

  return (
    <div className="story-container">
      <div className="header">
        <div className="image-overlay"></div>
        <img className="background-image" src="https://media.sleepdoctor.com/images/f_auto,q_auto:eco/v1675291188/thesleepdoctor-com/blind-man/blind-man.jpg?_i=AA" alt="Background" />
        <div className="overlay">
          <h1>Personal Stories</h1>
          <p>Discover the inspiring stories of individuals from various walks of life. Each story highlights unique challenges and triumphs, showcasing the resilience and determination that define their journeys. From overcoming personal obstacles to achieving professional success, these narratives offer valuable insights and inspiration for everyone.</p>
        </div>
      </div>
      <div className="content">
        <div className="people-list">
          {filteredPeople.map((person, index) => (
            <div className={`person-card ${flipped[index] ? 'flipped' : ''}`} key={index} onClick={() => handleFlip(index)}>
              <div className="person-card-inner">
                <div className="person-card-front">
                  <div className="person-image">
                    <img src={person.img} alt={person.name} />
                  </div>
                  <h2>{person.name}</h2>
                </div>
                <div className="person-card-back">
                  <h2>{person.name}</h2>
                  <p>{person.story}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
        <div className="filter-options">
          <button onClick={() => setFilter('male')}>Meet Men</button>
          <button onClick={() => setFilter('female')}>Meet Women</button>
          <button onClick={() => setFilter('')}>Show All</button>
        </div>
      </div>
    </div>
  );
};

export default Story;
